css:
.header {
  background: #a7beae !important;
}
.header__toggler-icon {
  color: #699686 !important;
}
.header__nav a{
 color: #699686 !important;
}
#cart-button .fa-shopping-bag::before, #user-button .fa-user::before {
  background-color: #699686 !important;;
}
#cart-count {
  background: transparent
}

.product-card {
  height: 0%; important!
  padding var(--spacer-) important!
}
@media (max-width:767px) {
    .product-list > .col-sm-6, .product-page + .grid-title + hr + .row > .col-sm-6 {
        width: 50%;
        float: left
    }
     .product-item { height: 280px !important; } 

}

#voltar {
border: 1px solid #10736e;
}

@media(min-width:576px)
{.header__logo
  {max-height:60px;
   max-width:100px
     }
  @media(min-width:800px)
{.header__logo
  {max-height:60px;
   max-width:150px
     }
}

.header__nav {
   text-align: left !important;
    padding: 22px 12px 23px !important;
}

@media screen and (max-width:576px){
.banner picture{
       margin: 0.1cm !important;
  }
}
@media screen and (min-width:576px){
.banner picture{
       margin: 0.5cm !important;
  }
}
.minicart__subtotal{
  color:var(--primary)!important;
}
.btn-outline-secondary{
  border-color:var(--primary)!important;
  color:var(--primary)  !important
}
.app__header{
   background: #699686 !important;
}
.summary__amount__row--total strong {
  color:var(--primary)!important;
}

.search-engine__toggles button {
  color:var(--primary)!important;
}

@media (max-width: 700px) {
.prices__discount 
    color: #699686 !important;
}

.prices__discount-label {
  color: #699686 !important;
}


.element.style {
    background-color: #50b448;
}

 .prices {
display: flex;
flex-direction: column;
}

.prices__compare {
order: 1;
font-size: var(--font-size-lg);
}

.prices__value + span {
order: 2;
display: flex;
flex-direction: column;
font-size: var(--font-size-lg);
}

.prices__value {
order: 3;
font-size: var(--font-size-sm);
}

.prices__installments {
order: 1;
font-size: var(--font-size-sm);
}

/* Ajustes para mobile */
@media (max-width: 1199px) {
.prices {
flex-direction: column;
}

.prices__compare {
order: 1;
font-size: var(--font-size-md); /* Ajuste para mobile, se necessário */
}

.prices__value + span {
order: 2;
font-size: var(--font-size-md) !important;
}

.prices__value {
order: 3;
font-size: var(--font-size-sm);
display: block; /* Garantindo que seja exibido no mobile */
}

.prices__installments {
order: 4; /* Ajuste para manter a organização no mobile */
}
}

/* Ajustes para desktop */
@media (min-width: 1200px) {
.prices__value {
order: 3;
font-size: var(--font-size-sm) !important;
}
}

/* Escondendo o preço por padrão */
.prices__value {
display: none;
}

  .payment-option__installment li:after, .prices__installments span:after {
    content: " no cartão";
}


  .prices__value {
    display: none !important;
    font-size: var(--font-size-lg);
}

@media (max-width: 575.98px) {
.col, .col-1, .col-10, .col-11, .col-12, .col-2, .col-3, .col-4, .col-5, .col-6, .col-7, .col-8, .col-9, .col-auto, .col-lg, .col-lg-1, .col-lg-10, .col-lg-11, .col-lg-12, .col-lg-2, .col-lg-3, .col-lg-4, .col-lg-5, .col-lg-6, .col-lg-7, .col-lg-8, .col-lg-9, .col-lg-auto, .col-md, .col-md-1, .col-md-10, .col-md-11, .col-md-12, .col-md-2, .col-md-3, .col-md-4, .col-md-5, .col-md-6, .col-md-7, .col-md-8, .col-md-9, .col-md-auto, .col-sm, .col-sm-1, .col-sm-10, .col-sm-11, .col-sm-12, .col-sm-2, .col-sm-3, .col-sm-4, .col-sm-5, .col-sm-6, .col-sm-7, .col-sm-8, .col-sm-9, .col-sm-auto, .col-xl, .col-xl-1, .col-xl-10, .col-xl-11, .col-xl-12, .col-xl-2, .col-xl-3, .col-xl-4, .col-xl-5, .col-xl-6, .col-xl-7, .col-xl-8, .col-xl-9, .col-xl-auto {
    padding-left: 0px !important;
    padding-right: 0px !important;
   }
  }
  /* Ajuste para mobile, tamanho do nome da categoria */
  @media (max-width: 575.98px) {
    .h1, h1 {
        font-size: calc(1.1rem + 1.1vw);
    }
}

